import './Collaborator.css';
import { FaWindowClose, FaHeart, FaRegHeart } from "react-icons/fa";

const Collaborator = (props) => {
    /* States */
    const {nombre, puesto, foto, equipo, id, fav, favCollaborator} = props.value;
    const {mainColor, removeCollaborator} = props

    const like = () => {
        if(fav === true){
            fav = false
        } else {
            fav = true
        }
    }
    return <div className='collaborator' style={{backgroundColor : mainColor}}>
        <FaWindowClose className='closeButton' onClick={() =>removeCollaborator(id) } />
        <div className='collaboratorHeader'>
            <img src={foto} alt={nombre}/>
            <h3>{nombre}</h3>
            <p>{puesto}</p>
            {fav ? <FaHeart 
                className='fullHeart'
                onClick={like}
                /> :
                <FaRegHeart
                className='emptyHeart'
                onClick={like}
                />}
        </div>
    </div>
};

export default Collaborator;
