import { useState } from 'react';
import './App.css';
import Header from './components/Header/Header';
import Form from './components/Form/Form';
import MyOrg from './components/MyOrg/MyOrg';
import Equipment from './components/Equipment/Equipment';
import Footer from './components/Footer/Footer';

function App() {
  /* Equipment list */
  const equipmentsDataList = [
    {
      tittle: 'Programación',
      mainColor: '#57C278',
      secondaryColor: '#D9F7E9'
    },
    {
      tittle: 'Front End',
      mainColor: '#82CFFA',
      secondaryColor: '#E8F8FF'
    },
    {
      tittle: 'Data Science',
      mainColor: '#A6D157',
      secondaryColor: '#F0F8E2'
    },
    {
      tittle: 'Devops',
      mainColor: '#E06B69',
      secondaryColor: '#FDE7E8'
    },
    {
      tittle: 'UX y Diseño',
      mainColor: '#DB6EBF',
      secondaryColor: '#FAE9F5'
    },
    {
      tittle: 'Móvil',
      mainColor: '#FFBA05',
      secondaryColor: '#FFF5D9'
    },
    {
      tittle: 'Innovación y  Gestión',
      mainColor: '#FF8A29',
      secondaryColor: '#FFEEDF'
    },
  ]

  /* States */
  const [showForm, updateShowForm] = useState(false);
  const [dataCollaborator, updateDataCollaborator] = useState([{
    nombre: "Génesis",
    equipo: "Programación",
    puesto: "Desarrolladora",
    foto: "https://example.com/genesis.jpg"
    },
    {
    nombre: "Christian",
    equipo: "Programación",
    puesto: "Desarrollador",
    foto: "https://example.com/christian.jpg"
    },
    {
    nombre: "Luri",
    equipo: "Front End",
    puesto: "Desarrolladora",
    foto: "https://example.com/luri.jpg"
    },
    {
    nombre: "Jean Marie",
    equipo: "UX y Diseño",
    puesto: "Diseñadora",
    foto: "https://example.com/jeanmarie.jpg"
    },
    {
    nombre: "José González",
    equipo: "Innovación y gestión",
    puesto: "Gestor",
    foto: "https://example.com/josegonzalez.jpg"
    }]);

  const functionUpdateShowForm = () => {
    updateShowForm(!showForm)
  }

  /* Recibir colaboradores */
  const getCollaborators = (collaborators) => {
    console.log('recibiendo', collaborators);
    updateDataCollaborator([... dataCollaborator, collaborators])
  }

  return (
    <div className="App">
      <Header/>
      {/* { showForm ? <Form/> : <></> } */}
      { showForm && <Form equipments = {
          equipmentsDataList.map((equipments) => equipments.tittle)
        }
        getCollaborators = {getCollaborators} /> }
      <MyOrg formSwitch = {functionUpdateShowForm}/>
      {
        equipmentsDataList.map((equipments) => {
          return <Equipment
          tittle = {equipments.tittle}
          key = {equipments.tittle}
          mainColor = {equipments.mainColor}
          secondaryColor = {equipments.secondaryColor}
          dataCollaborator = {dataCollaborator.filter(collaborator => collaborator.equipo === equipments.tittle)}
          />
        })
      }
      <Footer/>
    </div>
  );
}

export default App;
